function diou=boxdiou(x1,y1,w1,h1,x2,y2,w2,h2)    % compute distance intersection over union% % (C) Anton Andriyenko, 2012%% The code may be used free of charge for non-commercial and% educational purposes, the only requirement is that this text is% preserved within the derivative work. For any other purpose you% must contact the authors for permission. This code may not be% redistributed without written permission from the authors.     iou=boxiou(x1,y1,w1,h1,x2,y2,w2,h2);    pen = boxdiouPenalty(x1,y1,w1,h1,x2,y2,w2,h2);        diou = 1 - iou + pen;    if diou < 2            %disp(dist);      %disp(iou);    endend