function penalty=boxdiouPenalty(x1,y1,w1,h1,x2,y2,w2,h2)    % compute diou penalty term% % (C) Anton Andriyenko, 2012%% The code may be used free of charge for non-commercial and% educational purposes, the only requirement is that this text is% preserved within the derivative work. For any other purpose you% must contact the authors for permission. This code may not be% redistributed without written permission from the authors.     distX1 = x1+w1/2;    distX2 = x2+w2/2;    distY1 = y1+h1/2;    distY2 = y2+h2/2;    centerToCenter = (distX1-distX2)*(distX1-distX2)+(distY1-distY2)*(distY1-distY2);        %coordinates of corners of smallest enclosing box covering the two boxes    maxX = max(x1+w1,x2+w2);    maxY = max(y1+h1,y2+h2);    minX = min(x1,x2);    minY = min(y1,y2);            %diagonal length of the smallest enclosing box covering the two boxes    squareOfDiag = ((maxX-minX)*(maxX-minX))+((maxY-minY)*(maxY-minY)); %Euclidean distance        penalty = centerToCenter/squareOfDiag;    if penalty < .5      %dist = boxdist(x1,y1,w1,h1,x2,y2,w2,h2);      %iou = boxiou(x1,y1,w1,h1,x2,y2,w2,h2);      %disp(dist);      %disp(iou);    endend